class App extends React.Component {
  state = {
    count: 0
  };

  add = () => {
    this.setState({
      cnt: 1
    });
  }

  render() {
    App.add();
    console.log(this.state);
    return (
      <div>
        <h1>{this.state.count}</h1>
        <button onClick={this.add}></button>
      </div>
    )
  };
}


export default App;
